#!/usr/bin/env python3# -*- coding: utf-8 -*-"""Created on Wed Jan 26 07:24:05 2022@author: William Bennett"""import numpy as np###############################################################################class mesh:    def __init__(self, N_space, x0, source, tfinal, uncollided, moving):        self.tfinal = tfinal        self.N_space = N_space        self.x0 = x0        self.source = source        self.uncolloded = uncollided        self.moving = moving        self.edges = np.zeros(N_space+1)        self.Dedges = np.zeros(N_space+1)        self.initialize_mesh()                    def move(self, t):        if self.moving == "linear":            self.Dedges = self.edges/self.edges[-1]            self.edges += self.Dedges*np.abs(self.edges)*t                def initialize_mesh(self):        if self.moving != "static":            self.edges = np.linspace(-self.x0, self.x0, self.N_space+1)        else:            self.edges = np.linspace(-self.tfinal, self.tfinal, self.N_space+1)        